import { SearchContext } from "./../../store/SearchContext/SearchContext";
import React, { useContext } from "react";
import "./Search.css";
import { FaSearch } from "react-icons/fa";

function Search(props) {
  // const [searchedValue, setSearchedValue] = useState("pasta");
  const {searchValue, handleSearch} = useContext(SearchContext);
  console.log("searchCtx", searchValue)
  const handleInputChange = (e) => {
    handleSearch(e.target.value)
  }
  const handleFormSubmit = (e) => {
    e.preventDefault();
    // props.getRecipes(searchedValue);
  };
  return (
    <form className="search-form" onSubmit={handleFormSubmit}>
      <div>
        <input className="search__input" type="text" placeholder="Search"  value = {searchValue} onChange={handleInputChange}/>
        <div className="search-icon">
          <FaSearch />
        </div>
      </div>
    </form>
  );
}

export default Search;
