import React, { useState, useContext, useEffect } from "react";
import RecipeItems from "./RecipeItems";
import "./Recipes.css";
import { SearchContext } from "../../store/SearchContext/SearchContext";
import Axios from "axios";

const DUMMY_RECIPES = [
  {
    id: 716429,
    title: "Pasta with Garlic, Scallions, Cauliflower & Breadcrumbs",
    image: "https://spoonacular.com/recipeImages/716429-312x231.jpg",
    imageType: "jpg",
  },
  {
    id: 715538,
    title: "What to make for dinner tonight?? Bruschetta Style Pork & Pasta",
    image: "https://spoonacular.com/recipeImages/715538-312x231.jpg",
    imageType: "jpg",
  },
  {
    id: 716434,
    title: "Pasta with Garlic, Scallions, Cauliflower & Breadcrumbs",
    image: "https://spoonacular.com/recipeImages/716429-312x231.jpg",
    imageType: "jpg",
  },
  {
    id: 716784,
    title: "Pasta with Garlic, Scallions, Cauliflower & Breadcrumbs",
    image: "https://spoonacular.com/recipeImages/716429-312x231.jpg",
    imageType: "jpg",
  },
  {
    id: 716684,
    title: "Pasta with Garlic, Scallions, Cauliflower & Breadcrumbs",
    image: "https://spoonacular.com/recipeImages/716429-312x231.jpg",
    imageType: "jpg",
  },
];

const Recipes = () => {
  const { searchValue } = useContext(SearchContext);
  const [recipes, setRecipes] = useState([]);

  const url = `https://api.spoonacular.com/recipes/complexSearch?apiKey=${process.env.REACT_APP_API_KEY}&query=${searchValue}&number=10`;
  const getRecipes = async () => {
    await Axios.get(url, {
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((res) => setRecipes(res.data?.results))
      .catch();
  };
  useEffect(() => {
    getRecipes();
  }, []);
  return (
    <React.Fragment>
      <div className="recipes-container">
        {DUMMY_RECIPES.map((recipe) => (
          <RecipeItems key={recipe.id} recipe={recipe} />
        ))}
      </div>
    </React.Fragment>
  );
};

export default Recipes;
