import React from "react";
import Card from "./Card";
import ReactDOM  from "react-dom";
import './Modal.css'

const Backdrop = (props) => {
  return <div className="backdrop" onClick={props.onConfirm}></div>

};

const ModalOverlay = (props) => {
  return (
    <div className="modal-overlay">
      <div>
        <h1>This is the modal</h1>
      </div>
    </div>
  );
};

const Modal = (props) => {
    console.log(props)
  return (
    <React.Fragment>
      {ReactDOM.createPortal(<Backdrop onConfirm={props.onConfirm} />, document.getElementById("backdrop-modal"))}
      {ReactDOM.createPortal(<ModalOverlay title={props.title} message={props.message} onConfirm={props.onConfirm} />,
        document.getElementById("overlay-modal")
      )}
    </React.Fragment>
  );
};

export default Modal;
