import { createStore } from "redux";

const initialState = {
  favourites: [],
  totalCount: 0,
  isFav: false,
};
const FavouriteReducer = (state = initialState, action) => {
  console.log("STATE", state.favourites);

  if (action.type === "ADD_FAV") {
    return {
      ...state,
      favourites: [...state.favourites, action.payload],
    };
  }
  if (action.type === "REMOVE_FAV") {
    return state.favourites.filter((fav) => fav.id !== action.id);
  }
  if (action.type === "IS_FAVOURITE") {
    console.log("am inside")
    return {
      ...state,
      isFav: state.favourites.some((fav) => fav.id === action.payload.id),
    };
  }
};

const store = createStore(FavouriteReducer);

export default store;
